WHERE user_id = #{userId} AND subject_id = #{subjectId}
AND uploader_id = #{uploaderId}

DELETE FROM exam_bank_collection 
WHERE user_id = #{userId} AND subject_id = #{subjectId}
AND uploader_id = #{uploaderId}
