mapper.select:
SELECT 
FROM exam_bank


        AND subject LIKE CONCAT('%', #{subject}, '%')


        AND subject_id = #{subjectId}


        AND major_id = #{majorId}


        AND major_name LIKE CONCAT('%', #{majorName}, '%')


        AND uploader_id = #{uploaderId}


        AND status = #{status}


        AND bank_name LIKE CONCAT('%', #{bankName}, '%')


ORDER BY upload_time DESC

SELECT COUNT(*)
FROM exam_bank


        AND subject LIKE CONCAT('%', #{subject}, '%')


        AND subject_id = #{subjectId}


        AND major_id = #{majorId}


        AND major_name LIKE CONCAT('%', #{majorName}, '%')


        AND uploader_id = #{uploaderId}


        AND status = #{status}


        AND bank_name LIKE CONCAT('%', #{bankName}, '%')

SELECT 
FROM exam_bank


        AND subject LIKE CONCAT('%', #{examBank.subject}, '%')


        AND subject_id = #{examBank.subjectId}


        AND major_id = #{examBank.majorId}


        AND major_name LIKE CONCAT('%', #{examBank.majorName}, '%')


        AND uploader_id = #{examBank.uploaderId}


        AND status = #{examBank.status}


        AND bank_name LIKE CONCAT('%', #{examBank.bankName}, '%')


ORDER BY upload_time DESC
LIMIT #{offset}, #{limit}
