mapper.select:
SELECT 
FROM exam_record


        AND user_id = #{userId}


        AND subject_id = #{subjectId}


        AND paper_id = #{paperId}


ORDER BY exam_time DESC

SELECT 
FROM exam_record
WHERE user_id = #{userId}
ORDER BY exam_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM exam_record
WHERE user_id = #{userId}
