mapper.select:
SELECT 
FROM question


        AND subject_id = #{subjectId}


        AND type = #{type}


        AND content LIKE CONCAT('%', #{content}, '%')


        AND uploader_id = #{uploaderId}


ORDER BY created_time DESC

SELECT 
FROM question
WHERE subject_id = #{subjectId}

    AND type = #{type}

ORDER BY RAND()
LIMIT #{limit}

SELECT 
FROM question
WHERE subject_id = #{subjectId}

    AND difficulty = #{difficulty}

ORDER BY RAND()
LIMIT #{limit}

SELECT 
FROM question
WHERE subject_id = #{subjectId}

    AND type = #{type}


    AND difficulty = #{difficulty}

ORDER BY RAND()
LIMIT #{limit}

SELECT 
FROM question
WHERE id IN

    #{id}

ORDER BY FIELD(id,

    #{id}
)
